svg.script:

test(function() {
  let svg = document.getElementById("svg");
  let circle = document.getElementById("circle");
  let hitTest = function(pt) {
    return document.elementFromPoint(
            pt.x * svg.width.baseVal.value / svg.viewBox.baseVal.width,
            pt.y * svg.height.baseVal.value / svg.viewBox.baseVal.height);
  }
  assert_equals(hitTest(circle.getPointAtLength(0)), circle, "hit-test the beginning of the dash");
  assert_equals(hitTest(circle.getPointAtLength(5)), circle, "hit-test the middle of the dash");
  assert_equals(hitTest(circle.getPointAtLength(9.8)), circle, "hit-test the end of the dash");
  assert_equals(hitTest(circle.getPointAtLength(10.2)), svg, "hit-test past the end of the dash");
});
