svg.script:

test(function() {
  let circle = document.getElementById("circle");
  assert_true(circle.isPointInStroke(circle.getPointAtLength(0)), "hit-test the beginning of the dash");
  assert_true(circle.isPointInStroke(circle.getPointAtLength(5)), "hit-test the middle of the dash");
  assert_true(circle.isPointInStroke(circle.getPointAtLength(9.8)), "hit-test the end of the dash");
  assert_false(circle.isPointInStroke(circle.getPointAtLength(10.2)), "hit-test past the end of the dash");
});
